INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Make a poppy pin-wheel', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179161531', 'Anzac Square, Brisbane City', '2025-01-05T11:00:00+11:00', '2025-01-05T17:00:00+11:00', 'Sunday, 5 January 2025, 10am - 4pm', 'Visit Anzac Square Memorial Galleries these school holidays to make a poppy pin-wheel and discover the significance of poppies.

A resilient flower, the red poppy was amongst the first to return to bloom in the fields of France and Belgium after World War I, emerging as a symbol of remembrance for the Allied soldiers who fell in battle.

Visit our Post World War II Memorial and Exhibition Gallery between Sunday 15 December 2024 to Sunday 26 January 2025 (closed Saturdays and public holidays) and, on our craft table, you''ll find everything you need to make your very own poppy pin-wheel.

