INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Journey to the Stars', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179295354', 'Sir Thomas Brisbane Planetarium, Mt Coot-tha', '2025-01-08T13:00:00+11:00', '2025-01-08T13:45:00+11:00', 'Wednesday, 8 January 2025, 12 - 12:45pm', 'This dazzling space show launches visitors through space and time to experience the life and death of the stars in our night sky, including our own nurturing Sun. Tour familiar stellar formations, explore new celestial mysteries, and discover the fascinating, unfolding story that connects us all to the stars. Those who come along for the journey may never see the night sky in the same way again. Narrated by Whoopi Goldberg, Session concludes with a virtual tour of the Brisbane night sky.

