INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Don Ross mosaic on display for a limited time', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179180156', 'Anzac Square, Brisbane City', '2025-01-14T11:00:00+11:00', '2025-01-14T17:00:00+11:00', 'Tuesday, 14 January 2025, 10am - 4pm', 'Visit Anzac Square Memorial Galleries between 6-23 January to see the colourful Venetian glass mosaic created by late Brisbane artist, Don Ross. Entry is free.

This powerful tribute to the men and women who served in World War II is only revealed twice per year – in January (to coincide with the artist''s birthday) and during Brisbane Open House. It features an astounding 140,000 hand cut Smalti Tessera (Venetian glass) tiles.

Anzac Square Memorial Galleries is open 10am-4pm, Sunday to Friday (closed Saturdays and public holidays).

Visitors can stay to explore the Memorial Galleries - embarking on an interactive journey from the First World War to today, before taking time to reflect on the remarkable stories of bravery and sacrifice in the tranquil parklands.

Anzac Square Memorial Galleries is located at 285 Ann Street, Brisbane. Entry is through the double brass doors located beneath the Shrine of Remembrance.', 'Brisbane City Council', 'Art', 'Anzac Square, Brisbane City', 'Anzac Square, 228 Adelaide Street, Brisbane City', 'Non-Council venue', NULL, 'Art', 'Free', 'https://www.trumba.com/i/DgDnXmwMkKYaLPOafjBXmEjK.jpg', 'All ages', 'No bookings required. For more information, visit the <a href="https://www.anzacsquare.qld.gov.au/whats-on/don-ross-mosaic-display-limited-time-2" target="_blank">website</a>.', TRUE, 'Infants and toddlers,Preschool kids,Kids,Teens,Young adults,Adults (30+),Seniors', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Holiday fun', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179276239', 'State Library of Queensland, Stanley Place, South Brisbane', '2025-01-14T11:00:00+11:00', '2025-01-14T16:00:00+11:00', 'Tuesday, 14 January 2025, 10am - 3pm', 'These school holidays, come to the State Library of Queensland to explore sustainability with a twist!

Enjoy hands-on activities for all ages, including Make it, change it, save it, presented by State Library of Queensland and developed in collaboration with Reverse Garbage Queensland to create playful worlds using repurposed objects. Visit kuril dhagun and experience the vibrant world of Deaf in dance showcase and create your own portrait. Plus, enjoy our Scavenger hunt and cozy Reading room for the perfect blend of exploration and relaxation.

