INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Don Ross mosaic on display for a limited time', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179180157', 'Anzac Square, Brisbane City', '2025-01-15T11:00:00+11:00', '2025-01-15T17:00:00+11:00', 'Wednesday, 15 January 2025, 10am - 4pm', 'Visit Anzac Square Memorial Galleries between 6-23 January to see the colourful Venetian glass mosaic created by late Brisbane artist, Don Ross. Entry is free.

This powerful tribute to the men and women who served in World War II is only revealed twice per year – in January (to coincide with the artist''s birthday) and during Brisbane Open House. It features an astounding 140,000 hand cut Smalti Tessera (Venetian glass) tiles.

Anzac Square Memorial Galleries is open 10am-4pm, Sunday to Friday (closed Saturdays and public holidays).

