INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Nature''s Creatures: Eco Sculpture Workshop (10yrs and under)', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179161218', 'RQAS Petrie Terrace Gallery, 3/162 Petrie Terrace, Brisbane 4000', '2025-01-19T10:30:00+11:00', '2025-01-19T13:30:00+11:00', 'Sunday, 19 January 2025, 9:30am - 12:30pm', 'Immerse yourself in the beauty of the natural world by creating your own unique eco sculpture using materials sourced directly from nature.

In this hands-on workshop, you’ll use an array of natural elements—sticks, leaves, palm fronds, seed pods, vines, and more—to design and craft a one-of-a-kind creature that could almost be alive! Whether you''re looking to create a whimsical animal for your garden, a mystical creature for your bedroom, or a stunning decoration for your fence, this workshop will guide you through the process of transforming simple natural materials into intricate, expressive sculptures.

(Separate sessions available for children aged 11 years and over.)

NOTE: Hot glue guns will be used in this workshop. If your child is aged 8 or under, or you are concerned we ask that you stay to assist them
Join us for a fun, eco-friendly, and inspiring experience as we celebrate the wonders of nature through art! Make sure to purchase tickets soon as spaces are limited!

The Royal Queensland Art Society is proudly supported by Brisbane City Council. For more information, please visit the RQAS website.', 'Brisbane City Council', 'Art,Creative,School holidays,Workshops', NULL, NULL, NULL, NULL, 'School holidays', '$50', 'https://www.trumba.com/i/DgCT4t179dGUDCBb6VYEHZx%2A.jpg', '10 years and under', 'Bookings required. To book, visit <a href="https://events.humanitix.com/nature-s-creatures-eco-sculpture-workshop-10yrs-and-under-sessions?hxchl=hex-pfl" target="_blank">Humanitix.</a>', TRUE, 'Preschool kids,Kids', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO events (subject, web_link, location, start_datetime, end_datetime, formatteddatetime, description, event_template, event_type, venue, venueaddress, venuetype, parentevent, primaryeventtype, cost, eventimage, age, bookings, bookingsrequired, agerange, libraryeventtypes, eventtype, status, maximumparticipantcapacity, activitytype, requirements, meetingpoint, waterwayaccessfacilities, waterwayaccessinformation, communityhall, image) VALUES ('Don Ross mosaic on display for a limited time', 'https://www.brisbane.qld.gov.au/trumba?trumbaEmbed=view%3devent%26eventid%3d179180161', 'Anzac Square, Brisbane City', '2025-01-19T11:00:00+11:00', '2025-01-19T17:00:00+11:00', 'Sunday, 19 January 2025, 10am - 4pm', 'Visit Anzac Square Memorial Galleries between 6-23 January to see the colourful Venetian glass mosaic created by late Brisbane artist, Don Ross. Entry is free.

This powerful tribute to the men and women who served in World War II is only revealed twice per year – in January (to coincide with the artist''s birthday) and during Brisbane Open House. It features an astounding 140,000 hand cut Smalti Tessera (Venetian glass) tiles.

Anzac Square Memorial Galleries is open 10am-4pm, Sunday to Friday (closed Saturdays and public holidays).

Visitors can stay to explore the Memorial Galleries - embarking on an interactive journey from the First World War to today, before taking time to reflect on the remarkable stories of bravery and sacrifice in the tranquil parklands.

Anzac Square Memorial Galleries is located at 285 Ann Street, Brisbane. Entry is through the double brass doors located beneath the Shrine of Remembrance.', 'Brisbane City Council', 'Art', 'Anzac Square, Brisbane City', 'Anzac Square, 228 Adelaide Street, Brisbane City', 'Non-Council venue', NULL, 'Art', 'Free', 'https://www.trumba.com/i/DgDnXmwMkKYaLPOafjBXmEjK.jpg', 'All ages', 'No bookings required. For more information, visit the <a href="https://www.anzacsquare.qld.gov.au/whats-on/don-ross-mosaic-display-limited-time-2" target="_blank">website</a>.', TRUE, 'Infants and toddlers,Preschool kids,Kids,Teens,Young adults,Adults (30+),Seniors', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
